    private void SAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SAddActionPerformed
        // TODO add your handling code here:
    }//GEN-LAST:event_SAddActionPerformed
        Connection con1;
        PreparedStatement insert;
        private void table_update(){
            int c;
            try {
            Class.forName("com.mysql.jdbc.Driver");
            con1 = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/bashir_swings","root","1234");
            insert = con1.prepareStatement("SELECT * FROM studentregistration s;");
            ResultSet rs = insert.executeQuery();
            ResultSetMetaData Rss = rs.getMetaData();
            c = Rss.getColumnCount();
            DefaultTableModel dr = (DefaultTableModel) StudentTable.getModel();
            dr.setRowCount(0);
            while(rs.next()){
                Vector v2 = new Vector();
                for(int a = 1; a <= c; a++){
                    v2.add(rs.getString("Id"));
                    v2.add(rs.getString("Name"));
                    v2.add(rs.getString("Course"));
                    v2.add(rs.getString("Mobile"));
                    
                    
                }
                dr.addRow(v2);
            }
 
        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(StudentRegistrationForm.class.getName()).log(Level.SEVERE, null, ex);
        }
        }
    private void SAddMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SAddMouseClicked
        // TODO add your handling code here:
        String sname = sn.getText();
        String scourse = sc.getText();
        String smobile = sm.getText();
        
        
        try {
            Class.forName("com.mysql.jdbc.Driver");
            con1 = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/bashir_swings","root","1234");
            insert = con1.prepareStatement("INSERT INTO studentregistration (Name,Course,Mobile) VALUES (?,?,?)");
            insert.setString(1, sname);
            insert.setString(2, scourse);
            insert.setString(3, smobile);
            insert.executeUpdate();
            JOptionPane.showMessageDialog(this, "SAVED");
            table_update();
            
                
                System.out.println("Name   :\t"+sname);
                System.out.println("Course :\t"+scourse);
                System.out.println("Mobile :\t"+smobile);
            sn.setText(" ");
            sc.setText(" ");
            sm.setText(" ");
            sn.requestFocus();

            
        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(StudentRegistrationForm.class.getName()).log(Level.SEVERE, null, ex);
        }


    }//GEN-LAST:event_SAddMouseClicked

    private void StudentTableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_StudentTableMouseClicked
       DefaultTableModel dr = (DefaultTableModel) StudentTable.getModel();
       int sindex = StudentTable.getSelectedRow();
       sn.setText((String) dr.getValueAt(sindex, 1));
       sc.setText((String) dr.getValueAt(sindex, 2));
       sm.setText((String) dr.getValueAt(sindex, 3));
    }//GEN-LAST:event_StudentTableMouseClicked

    private void SDeleteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SDeleteMouseClicked
        DefaultTableModel dr = (DefaultTableModel) StudentTable.getModel();
        int sindex = StudentTable.getSelectedRow();
        try {
            int sid = Integer.parseInt(dr.getValueAt(sindex, 0).toString());
            
            int dialogResult= JOptionPane.showConfirmDialog(null, "Do you want to delete?","Warning",JOptionPane.YES_NO_OPTION);
            if(dialogResult==JOptionPane.YES_OPTION){
                Class.forName("com.mysql.jdbc.Driver");
                con1 = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/bashir_swings","root","1234");
                insert = con1.prepareStatement("DELETE FROM studentregistration WHERE Id=?");
                
                insert.setInt(1, sid);
                insert.executeUpdate();
            JOptionPane.showMessageDialog(this, "DELETED");
            table_update();
            
                
                
            sn.setText(" ");
            sc.setText(" ");
            sm.setText(" ");
            sn.requestFocus();
            }
            
            
            
            
            
           
            
        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(StudentRegistrationForm.class.getName()).log(Level.SEVERE, null, ex);
        }
    
    }//GEN-LAST:event_SDeleteMouseClicked

    private void SEditMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SEditMouseClicked
        DefaultTableModel dr = (DefaultTableModel) StudentTable.getModel();
        int sindex = StudentTable.getSelectedRow();
        try {
            int sid = Integer.parseInt(dr.getValueAt(sindex, 0).toString());
            String sname = sn.getText();
            String scourse = sc.getText();
            String smobile = sm.getText(); 
            Class.forName("com.mysql.jdbc.Driver");
            con1 = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/bashir_swings","root","1234");
            insert = con1.prepareStatement("UPDATE studentregistration SET Name=?,Course=?,Mobile=? WHERE Id=?");
            insert.setString(1, sname);
            insert.setString(2, scourse);
            insert.setString(3, smobile);
            insert.setInt(4, sid);
            
            insert.executeUpdate();
            JOptionPane.showMessageDialog(this, "Updated");
            table_update();
            
                
                System.out.println("Name   :\t"+sname);
                System.out.println("Course :\t"+scourse);
                System.out.println("Mobile :\t"+smobile);
            sn.setText(" ");
            sc.setText(" ");
            sm.setText(" ");
            sn.requestFocus();
            
           
            
        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(StudentRegistrationForm.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_SEditMouseClicked

